html:
{##
  WARNING
  Be extra cautious when changing the contents of this file along with any CSS or JS that styles or manipulates this HTML.
  Due to the way Page Cache works, both logged-in and logged-out users may see pages with updated CSS and JS along with stale HTML.
  If needed, please split your PR into two or more parts to make all CSS / JS changes backwards-compatible.
  See https://jira.squarespace.net/browse/COE-1047 for details.
##}


<section id="comments" data-dynamic-strings>

<!--COMMENTS HEADER-->
<div class="header-controls">
  <div class="controls">

    <h3 class="comment-count">
      <span>{localizedStrings.comments} ({totalComments})</span>
    </h3>

    <div class="comment-controls">

      <span class="comment-sort">
        <span>{commentSortTitle}</span>
        <select class="hidden-ordering">
          <option value="1" {.section sortOldestFirst} selected="selected" {.end}>{localizedStrings.oldestFirst}</option>
          <option value="2" {.section sortNewestFirst} selected="selected" {.end}>{localizedStrings.newestFirst}</option>
          {.section likesAllowed}
          <option value="3" {.section sortMostLiked} selected="selected" {.end}>{localizedStrings.mostLiked}</option>
          <option value="4" {.section sortLeastLiked} selected="selected" {.end}>{localizedStrings.leastLiked}</option>
          {.end}
        </select>
      </span>
    </div> 
  </div>
</div>


<!--NEW COMMENT TOP-->
{.section newCommentAreaTop}
<div class="new-comment-area top-level-comment-area edit-mode{.if commentAnonAllowed}{.or} anon-comments-off{.end}">
<!--	<span class="squarespace-comment-login"></span>	<div class="new-comment-title">{localizedStrings.addComment}</div>-->
  <div class="input">
    <form action="#" method="post" class="comment-form">
      <textarea class="comment-input" name="comment" data-defaulttext="{localizedStrings.leaveACommentHere}" data-edited="false"></textarea>
    </form>
  </div>
  <div class="comment-btn-wrapper">

    <span class="btn-text preview-comment top-level-preview-btn">{localizedStrings.preview}</span>
    <!--toggle class on .new-comment-area to hide/show the appropriate button below
    <span class="btn-text edit-comment">{localizedStrings.edit}</span>-->


    {.section readOnly}
      <span class="comment-btn read-only sqs-system-button sqs-editable-button-font sqs-editable-button-shape sqs-button-element--primary">
        {localizedStrings.commentsRestricted}
      </span>
    {.or}
      {.section authenticatedAccount}
        <span class="comment-btn{.section source} {@}{.end} sqs-system-button sqs-editable-button-font sqs-editable-button-shape sqs-button-element--primary">
          {localizedStrings.postComment}
        </span>
      {.or}
        <span class="comment-btn sqs-system-button sqs-editable-button-font sqs-editable-button-shape sqs-button-element--primary">
          {localizedStrings.postComment}
        </span>
      {.end}
    {.end}
  </div>
</div>
{.end}





<!--COMMENT LIST-->
<div class="comment-list {.section noAvatars}avatars-hidden{.end}">
  {.repeated section comments}
    <div id="comment-{id}" class="comment clearfix {.section readOnly}read-only{.end} {.section parentId}reply-comment{.end} {.section unmoderated}unmoderated{.end} {.section flagged}flagged{.end} {.section likesAllowed}{.section liked}liked{.end}{.end}" {.section parentId}data-replyTo={@}{.end} {.section depth}data-depth="{@}"{.end} {.section id}data-commentid="{@}"{.end}>

      <div class="comment-header">
        <div class="user-info">
          <div class="avatar">
            {.section authorUrl}
              <a href="{@|htmlattr}" rel="nofollow">
            {.end}{#

            }{.eval
              @width = 34;
              @style = 'display:unset';
              @passthrough = 'elementtiming="system-comment-avatar"';
              @loader = 'raw';
              @src = '../images/default-avatar.png';
            }{#

            }{.if memberAccount.avatarAssetUrl}{#
              # avatarAssetUrl appends ?format=300w on production but not on QA, makes it work '?&' appears to work in both cases.
              # it is always displayed at 34px so we want to request 100w (not exact to overlap with existing images).
              }{.eval @src = memberAccount.avatarAssetUrl + '?&format=100w'}{#
            }{.end}{#

            # height is not specified because it avatar is width-contained and we have no dimensional data for the image
            }{.ctx @image src=@src width=@width style=@style sizes=@sizes passthrough=@passthrough loader=@loader}{#
            }{@image|apply sqs-image}{#

            }{.section authorUrl}
              </a>
            {.end}
          </div>
          {.section authorUrl}
            <a href="{@|htmlattr}" rel="nofollow">
          {.end}
            <span class="author">{authorName}</span>
          {.section authorUrl}
            </a>
          {.end}
          <span class="date"><time class="published" datetime="{addedOn|date %F}">{addedOn|timesince}</time></span>
          <span class="posting-text not-moderated{.section posting} visible{.end}">{localizedStrings.pending}</span>
          <span class="posting-text moderated{.section awaitingModeration} visible{.end}">{localizedStrings.awaitingModeration}</span>
          {.section unmoderated}<span class="unmoderated-text">{localizedStrings.unmoderated}</span>{.end}
          {.section likesAllowed}<span class="likes">&middot; {likes}</span>{.end}
         </div>
        <div class="controls">

          <span class="squarespace-comment-buttons">
            {.section flagsAllowed}<span title="{localizedStrings.report}" class="flag comment-control"></span>{.end}
            {.section likesAllowed}<span title="{localizedStrings.like}" class="like comment-control"></span>{.end}
            {.section threaded}<span title="{localizedStrings.reply}" class="reply comment-control"></span>{.end}
            {.section unmoderated} <span title="{localizedStrings.approve}" class="approve comment-control"></span>{.end}
            {.section moderationMode}<span title="{localizedStrings.delete}" class="delete comment-control"></span>{.end}
          </span>
        </div>
      </div>

      <div class="comment-body">{body}</div>



    </div>
      <!--NEW REPLY AREA-->

      <div class="reply-area-wrapper" {.section depth}data-depth="{@}"{.end} {.section id}data-commentid="{@}"{.end}>
        <div class="new-comment-area reply-area edit-mode">
          <div class="input">
            <form action="#" method="post" class="comment-form reply-form">
              <textarea class="comment-input reply-input" name="reply" data-defaulttext="{localizedStrings.replyToThisCommentHere}" data-edited="false"></textarea>
            </form>
          </div>

          <div class="comment-btn-wrapper">
          <span class="btn-text preview-comment">{localizedStrings.preview}</span>
        <!--<span class="btn-text edit-comment">{localizedStrings.edit}</span>-->


            {.section readOnly}
              <span class="comment-btn read-only sqs-system-button sqs-editable-button-font sqs-editable-button-shape sqs-button-element--primary">
                {localizedStrings.commentsRestricted}
              </span>
            {.or}
              {.section authenticatedAccount}
                <span class="comment-btn{.section source} {@}{.end} sqs-system-button sqs-editable-button-font sqs-editable-button-shape sqs-button-element--primary">
                  {localizedStrings.postReply}
                </span>
              {.or}
                <span class="comment-btn sqs-system-button sqs-editable-button-font sqs-editable-button-shape sqs-button-element--primary">
                  {localizedStrings.postReply}
                </span>
              {.end}
            {.end}
          </div>
        </div>
      </div>
  {.end}
</div>



</section>
